import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import ProductImage from './ProductImage';
import '../Styles/ProductDetails.css'
import ProductImageDesc from './ProductImageDesc';

const ProductDetails = () => {
  const { id } = useParams(); // Access the id parameter from the URL
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/products/${id}`);
        setProduct(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching product:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!product) {
    return (
    <div>
        <h1>Product not found</h1>
    </div>);
  }

  return (
    <div class='prod-details'>
        <div className='image'>
            <ProductImage url={product.variants[0].url} description={product.description}/>
        </div>
        <div className='image-desc'>
            <ProductImageDesc name={product.name} description={product.description} price={product.price}/>
            <p>Quantity : {product.quantity}</p>
        </div>
    </div>
  );
};

export default ProductDetails;
