import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import ProductImage from './ProductImage';
import '../Styles/ProductDetails.css';
import ProductImageDesc from './ProductImageDesc';
import '../Styles/CreateProductButton.css';
import VariantButton from './VariantButtons';
import CartButton from './CartButton';
import '../Styles/CartButton.css';
import { useRecoilState } from 'recoil';
import { cartState } from './CartState';

const ProductDetails = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedUrl, setSelectedUrl] = useState(null);
  const [cart, setCart] = useRecoilState(cartState);
  const [quantityInCart, setQuantityInCart] = useState(0);
  const [isAddingToCart, setIsAddingToCart] = useState(true);
  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem('cart')) || [];
    const storedQuantityInCart = JSON.parse(localStorage.getItem('quantityInCart')) || 0;
    setCart(storedCart);
    setQuantityInCart(storedQuantityInCart);
  }, []);

  // Save cart state and quantityInCart to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
    localStorage.setItem('quantityInCart', JSON.stringify(quantityInCart));
  }, [cart, quantityInCart]);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/products/${id}`);
        setProduct(response.data);
        setSelectedUrl(response.data.variants[0].url);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching product:', error);
        setLoading(false);
      }
    };
    fetchData();
  }, [id]);

  const handleVariantClick = (variant) => {
    setSelectedUrl(variant.url);
  };

  const incrementQuantity = () => {
    setQuantityInCart(quantityInCart + 1);
  };

  const decrementQuantity = () => {
    if (quantityInCart > 1) {
      setQuantityInCart(quantityInCart - 1);
    } else if (quantityInCart === 1) {
      // Remove product from cart if quantityInCart is 1
      setIsAddingToCart(true);
      setQuantityInCart(0);
      setCart(cart.filter(item => item.id !== product.id));
    }
  };

  const addToCart = () => {
    if (isAddingToCart && product) {
      setCart([...cart, { ...product, quantityInCart: 1 }]);
      setIsAddingToCart(false);
      setQuantityInCart(1); // Set quantityInCart to 1 when adding to cart
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!product) {
    return (
      <div>
        <h1>Product not found</h1>
      </div>
    );
  }

  const quantity = product.quantity;

  return (
    <div>
      <p>{cart}</p>
      <div className='cart-container'>
        <CartButton cartlength={cart.length} />
      </div>
      <div className='prod-details'>
        <div className='image'>
          <ProductImage url={selectedUrl} description={product.description} />
        </div>
        <div className='image-desc'>
          <ProductImageDesc name={product.name} description={product.description} price={product.price} />
          <div style={{ marginLeft: '20px' }}>
            {quantity <= 10 ? <p>Selling Fast</p> : quantity > 10 ? <p>Available</p> : <p>Unavailable</p>}
          </div>
          <div className='variant-buttons-container'>
            {product.variants.map((variant) => (
              <div key={variant.id}>
                <VariantButton variant={variant} onVariantClick={handleVariantClick} />
              </div>
            ))}
          </div>
          {isAddingToCart ? (
            <button className='prod-button' onClick={addToCart}>
              Add to Cart
            </button>
          ) : (
            <div>
              <button onClick={decrementQuantity}>-</button>
              <span>{quantityInCart}</span>
              <button onClick={incrementQuantity}>+</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;

