import React from 'react'
import { useState,useEffect } from 'react';
import axios from 'axios'
import { useMutation } from 'react-query';
export default function AddReview({id}) {
    const [reviews,setReviews]=useState(null);
    const addReviewMutation=useMutation((newReview)=>{
        axios.get(`http://localhost:4000/reviewsdata/${id}`)
        .then(response=>{
          setReviews(response.data);
        })
        .catch(error=>{
          console.log('Error fetching product data',error);
        })
    });


      const addReviewToProduct=()=>{
        const newReview={
            name: "Eve Johnson",
      email: "eve@gmail.com",
      rating: 5,
      review: "Fantastic product!"
        };
        addReviewMutation.mutate(newReview,{
            onSuccess:()=>{
                console.log('Review Added Successfullly');
            },
            onError:(error)=>{
                console.error('Error : ',error);
            }
        })
        }
        return (
            <div>
            <h1>Review Added</h1>
            </div>
        );

}
