import React, { useState } from 'react';
import CartCard from './CartCard'; // Import the CartCard component

const CartButton = ({ cartLength }) => {
  const [isCartVisible, setIsCartVisible] = useState(false);

  const toggleCartVisibility = () => {
    setIsCartVisible(!isCartVisible);
  };

  return (
    <div className="cart-dropdown">
      <button onClick={toggleCartVisibility}>Cart: {cartLength}</button>
      {isCartVisible && <CartCard />} {/* Show CartCard component when isCartVisible is true */}
    </div>
  );
};

export default CartButton;

