import React from 'react'
import * as Yup from 'yup'
import { Formik,Form,ErrorMessage,Field } from 'formik'
// import { useState } from 'react'
import { useMutation,useQueryClient } from 'react-query'
import axios from 'axios'
// import AddReview from './AddReview.js'


const initialValues={
    name:'',
    email:'',
    rating:'',
    review:''
}

const validationSchema=Yup.object({
    name: Yup.string().required('Name is Required'),
    email:Yup.string().email('Invalid email Format').required('Email is Required'),
    rating:Yup.number().required("Please give a rating").min(1,'Rating value must be greater than 1').max(5,'Rating value must be less than or equal to 5'),
    review:Yup.string().required('Review is Required')
})

export default function ReviewForm({id}) {
  const queryClient = useQueryClient();
  const addReviewMutation = useMutation(
    (newReview) => {
      return axios.get(`http://localhost:4000/reviewsdata/${id}`).then((response) => {
        const existingData = response.data;
        console.log(existingData);
        const updatedData = {
          ...existingData,
          reviewsofproduct: [...existingData.reviewsofproduct, newReview]
        };
        console.log(updatedData);
        return axios.post(`http://localhost:4000/reviewsdata/${id}`, updatedData);
      });
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['reviewsdata', id]);
      }
    }
  );
    const onSubmit=async (values,{resetForm})=>{
      console.log('Form values',values);
      const response = await axios.get("http://localhost:4000/reviewsdata/");
        console.log(response.data);
        axios.get("http://localhost:4000/reviewsdata/")
        .then(response => {
          const existingData = response.data;
          console.log(existingData);
          // const updatedData = {
          //   id:3,
          //   reviewsofproduct:[]
          // };
          // console.log(updatedData);
          console.log({id});
          console.log(existingData[id]);
          existingData[{id}].reviewsofproduct.push(values);
          axios.post(`http://localhost:4000/reviewsdata/${id}`, existingData)
            .then(response => {
              console.log('Product  added successfully:', response.data);
            })
            .catch(error => { 
              console.error('Error adding product:', error);
            });
        })
        .catch(error => {
          console.error('Error fetching existing data:', error);
        });
      // addReviewMutation.mutate(values, {
      //   onError: (error) => {
      //     console.error('Error adding review:', error);
      //   }
      // });
      resetForm();
  }
  return (
    <div>
        <h1>Form</h1>
        <h1>{id}</h1>
        <h1>hii</h1>
    <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={onSubmit}>
        <Form> 
            <div className='form-comp'>
            <label htmlfor='name'>Name</label>
            <Field type='text' 
                id='name' 
                name='name'
            />
            </div>
            <ErrorMessage name='name'/>
            <div className='form-comp'>
            <label htmlfor='email'>E-mail</label>
            <Field type='email' 
                id='email' 
                name='email' 
            />
            </div>
            <ErrorMessage name='email'/>
            <div className='form-comp'>
            <label htmlfor='rating'>Rating</label>
            <Field type='text' 
                id='rating' 
                name='rating' 
            />
            </div>
            <ErrorMessage name='rating'/>
            <div className='form-comp'>
            <label htmlfor='review'>Review</label>
            <Field type='text' 
                id='review' 
                name='review' 
            />
            </div>
            <ErrorMessage name='review'/>
            <button type='submit'>Submit</button>



        </Form>
    </Formik>
    {/* <AddReview/> */}

    </div>
  )
}
