    //         <div style={{marginLeft:'20px'}}>
    //         {
    //           quantity <= 10 ? (
    //             <p>Selling Fast</p>
    //           ) : quantity > 10 ? (
    //             <p>Available</p>
    //           ) : quantity === 0 ? (
    //             <p>Unavailable</p>
    //           ) : null
    //         }
    //         </div>
    //         <div style={{marginLeft:'10px'}}>
    //           <VariantButtons variants={product.variants} onVariantClick={handleVariantClick}/>
    //         </div>
    //         <button style={{marginLeft:'20px',marginTop:'20px'}} className="prod-button " >Add to Cart</button>
    //     </div>
    // </div>
  );
};

export default ProductDetails;
