// CartCard.js
import React from 'react';
import { useRecoilValue } from 'recoil';
import { cartState } from './CartState.js';
import ProductImage from './ProductImage';
import ProductImageDesc from './ProductImageDesc';

const CartCard = () => {
  const cart = useRecoilValue(cartState);

  return (
    <div>
      {cart.map(product => (
        <div key={product.id} className="cart-item">
          <ProductImage url={product.variant[0].url} description={product.description} />
          <ProductImageDesc name={product.name} price={product.price} />
        </div>
        // <div>
        //     <p>{product.id}</p>
        //     <p>{product.quantityInCart}</p>
        // </div> 
      ))}
    </div>
  );
};

export default CartCard;
