import React from 'react';
import axios from 'axios';
import { useQuery } from 'react-query';
import '../Styles//HomePage.css'
import ProductImage from './ProductImage';
import ProductImageDesc from './ProductImageDesc';
import { Link } from 'react-router-dom'

const HomePage = () => {
  const { data, isLoading, isError } = useQuery('products',  ()=>{
    return axios.get('http://localhost:4000/products');
  });

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (isError) {
    return <div>Error loading joke</div>;
  }

  return (
    <div>
      <div className='image-container'>
      {data?.data.map(product=>(
        <div key='product.id' className='image-item'>
          <Link to='/#'><ProductImage url={product.variants[0].url} desc={product.description} /></Link>
          <ProductImageDesc key={product.id} name={product.name} desc={product.description} price={product.price}/>
        </div>
      ))}
      </div>
    </div>
  );
};

export default HomePage;
