                {/* <ProductImage url={product.variants[0].url} description={product.description} /> */}

              <ProductImageDesc name={product.name} quantityInCart={product.quantityInCart} description={product.description} price={totalprice} />
              </div>
              </div>
            );
          })}   

        </div>
        );
}
