import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import ProductImage from './ProductImage';
import '../Styles/ProductDetails.css'
import ProductImageDesc from './ProductImageDesc';
import '../Styles/CreateProductButton.css'
import VariantButtons from './VariantButtons';

const ProductDetails = () => {
  const { id } = useParams(); 
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedVariant,setSelectedVariant]=useState()
  // useEffect(() => {
  //   const fetchData = async () => {
  //     try {
  //       const response = await axios.get(`http://localhost:4000/products/${id}`);
  //       setProduct(response.data);
  //       // const initialvariant=product.variants[0];
  //       selectedVariant(product.variants[0]);
  //       setLoading(false);
  //     } catch (error) {
  //       console.error('Error fetching product:', error);
  //       setLoading(false);
  //     }
  //   };
  //   fetchData();
  // }, [id]);
  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:4000/products/${id}`);
      setProduct(response.data);
      // const initialvariant=product.variants[0];
      selectedVariant(product.variants[0]);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching product:', error);
      setLoading(false);
    }
  };
  fetchData();
  const handleVariantClick=(variant)=>{
    setSelectedVariant(variant);
  }

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!product) {
    return (
    <div>
        <h1>Product not found</h1>
    </div>);
  }
  const quantity=product.quantity;
  return (
    // <div>
    // {/* <div>{selectedVariant.url}</div> */}

    // <div>hii</div>
    // </div>
    <div class='prod-details'>
        <div className='image'>
            <ProductImage url={selectedVariant.url} description={product.description}/>
        </div>
        <div className='image-desc'>
            <ProductImageDesc name={product.name} description={product.description} price={product.price}/>
            <div style={{marginLeft:'20px'}}>
            {
              quantity <= 10 ? (
                <p>Selling Fast</p>
              ) : quantity > 10 ? (
                <p>Available</p>
              ) : quantity === 0 ? (
                <p>Unavailable</p>
              ) : null
            }
            </div>
            <div style={{marginLeft:'10px'}}>
              <VariantButtons variants={product.variants} onVariantClick={handleVariantClick}/>
            </div>
            <button style={{marginLeft:'20px',marginTop:'20px'}} className="prod-button " >Add to Cart</button>
        </div>
    </div>
  );
};

export default ProductDetails;
