import React from 'react'
import * as Yup from 'yup'
import { Formik,Form,ErrorMessage,Field } from 'formik'
import { useState } from 'react'
import { useMutation } from 'react-query'
import axios from 'axios'

const submitReview = async ({ id, formData }) => {
    try {
      const response = await axios.get(`http://localhost:4000/reviewsdata/${id}`);
      const existingData = response.data;

      const newReview = {
        name: formData.name,
        email: formData.email,
        rating: formData.rating,
        review: formData.review,
      };

      // Add the new review to the 'reviewsofproduct' array
      existingData.reviewsofproduct.push(newReview);


      // Use axios.post to add a new review
      await axios.post(`http://localhost:4000/reviewsdata/${id}`);
      return newReview;
    } catch (error) {
      throw new Error(error.response.data.message || 'Something went wrong');
    }
  };



const initialValues={
    name:'',
    email:'',
    rating:'',
    review:''
}

const validationSchema=Yup.object({
    name: Yup.string().required('Name is Required'),
    email:Yup.string().email('Invalid email Format').required('Email is Required'),
    rating:Yup.number().required("Please give a rating").min(1,'Rating value must be greater than 1').max(5,'Rating value must be less than or equal to 5'),
    review:Yup.string().required('Review is Required')
})

export default function ReviewForm({id}) {
    // const {isFormSubmitted,setFormSubmitted}=useState(false);
    const mutation = useMutation(submitReview);
    const onSubmit = async (values, { resetForm }) => {
        try {
        // const id = {id}; 
        await mutation.mutateAsync({ id, formData: values });
        resetForm();
        } catch (error) {
        console.error('Error submitting form:', error);
        }
    };
  return (
    <div>
        <h1>Form</h1>
    <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={onSubmit}>
        <Form> 
            <div className='form-comp'>
            <label htmlfor='name'>Name</label>
            <Field type='text' 
                id='name' 
                name='name'
            />
            </div>
            <ErrorMessage name='name'/>
            <div className='form-comp'>
            <label htmlfor='email'>E-mail</label>
            <Field type='email' 
                id='email' 
                name='email' 
            />
            </div>
            <ErrorMessage name='email'/>
            <div className='form-comp'>
            <label htmlfor='rating'>Rating</label>
            <Field type='text' 
                id='rating' 
                name='rating' 
            />
            </div>
            <ErrorMessage name='rating'/>
            <div className='form-comp'>
            <label htmlfor='review'>Review</label>
            <Field type='text' 
                id='review' 
                name='review' 
            />
            </div>
            <ErrorMessage name='review'/>
            <button type='submit'>Submit</button>

        </Form>
    </Formik>
    {/* {isFormSubmitted && <div>Thank You!  Review Submitted</div>} */}
    </div>
  )
}
