import '../App.css';
import React, { useState } from 'react';
import { useMutation } from 'react-query';
import axios from 'axios';

const ReviewForm = ({id}) => {
  const [formData, setFormData] = useState({
    name: '',
    review: '',
    rating: '',
  });
  const newErrors={};
  const [isSubmitted, setIsSubmitted] = useState(false); // Track submission status

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.name) {
      newErrors.name = 'Please enter a name.';
    }

    if (!formData.review) {
        newErrors.review = 'Please enter a review';
    }
    // Validating rating
    if (!formData.rating) {
      newErrors.rating = 'rating is required.';
    }

    // Set the errors if there are any
    //setErrors(newErrors);
    document.getElementById('nameError').innerText = newErrors.name|| '';
    document.getElementById('reviewError').innerText = newErrors.review|| '';
    document.getElementById('ratingError').innerText = newErrors.rating || '';

    // If there are no errors, you can proceed with form submission
    if (Object.keys(newErrors).length === 0) {
      // Perform your signup logic here

      // Update the submission status
      setIsSubmitted(true);
    }

  };

  return (
    <div>
      <h2>Leave a review</h2>
      <h1>{id}</h1>
      <form onSubmit={handleSubmit}>
      <div>
  <label>Name:</label>
  <input
    type="text"
    name="name"
    placeholder="Enter Your Name"
    value={formData.name}
    onChange={handleInputChange}
  />
  <div className="extraSpace"></div>
  <div id="nameError" className="error"></div>
</div>
<div>
  <label>review:</label>
  <input
    type="review"
    name="review"
    placeholder="Enter Your review"
    value={formData.review}
    onChange={handleInputChange}
  />
  <div className="extraSpace"></div>
  <div id="reviewError" className="error"></div>
</div>
<div>
  <label>rating:</label>
  <input
    type="rating"
    name="rating"
    placeholder="Create rating"
    value={formData.rating}
    onChange={handleInputChange}
  />
  <div id="ratingError" className="error"></div>
</div>

        <button type="submit">Submit Review</button>
      </form>
      {isSubmitted && <p className="success">Success! Form submitted.</p>}
    </div>
  );
};

export default ReviewForm;
